sourcesContent:
import express from 'express';
import indexRouter from './routes/index';

const app = express();
const port = process.env.PORT || 5000;

app.use(express.json());

app.listen(port, () => {
  console.log(`Server running on port ${port}`);
});
app.use('/', indexRouter);

export default app;

